     5‚Üíclass FastingSession {
     6‚Üí    private var startTime as Time.Moment?;
     7‚Üí    private var elapsedTime as Number = 0;
     8‚Üí    private var isActive as Boolean = false;
     9‚Üí    private var timer as Timer;
    10‚Üí    private var biometricsTracker as BiometricsTracker;
    11‚Üí    private var notifications as FastingNotifications;
    12‚Üí    
    13‚Üí    // Callback for timer updates
    14‚Üí    private var onTimerUpdate as Method(timeElapsed as Number) as Void;    function initialize(timerCallback as Method(timeElapsed as Number) as Void) {
    15‚Üí        onTimerUpdate = timerCallback;
    16‚Üí        timer = new Timer.Timer();
    17‚Üí        biometricsTracker = new BiometricsTracker();
    18‚Üí        notifications = new FastingNotifications();
    19‚Üí    }
    20‚Üí
    21‚Üí    function startFast() as Void {
    22‚Üí        if (!isActive) {
    23‚Üí            startTime = Time.now();
    24‚Üí            isActive = true;
    25‚Üí            timer.start(method(:updateTimer), 1000, true);
    26‚Üí            Storage.setValue("fastStartTime", startTime.value());
    27‚Üí            Storage.setValue("isActiveFast", true);
    28‚Üí        }
    29‚Üí    }    function stopFast() as Void {
    30‚Üí        if (isActive && startTime != null) {
    31‚Üí            var endTime = Time.now();
    32‚Üí            var stats = biometricsTracker.getSessionStats(startTime, endTime);
    33‚Üí            
    34‚Üí            timer.stop();
    35‚Üí            isActive = false;
    36‚Üí            startTime = null;
    37‚Üí            elapsedTime = 0;
    38‚Üí            
    39‚Üí            Storage.setValue("isActiveFast", false);
    40‚Üí            Storage.deleteValue("fastStartTime");
    41‚Üí            
    42‚Üí            // Show summary
    43‚Üí            var hours = endTime.compare(startTime) / 3600.0;
    44‚Üí            var summary = Lang.format(
    45‚Üí                "Fast complete!\nDuration: $1$:$2$ hours\nAvg HR: $3$ bpm\nAvg Stress: $4$",
    46‚Üí                [
    47‚Üí                    hours.toNumber(),
    48‚Üí                    ((hours % 1) * 60).format("%02d"),
    49‚Üí                    stats["avgHeartRate"] != null ? stats["avgHeartRate"].format("%.0f") : "--",
    50‚Üí                    stats["avgStress"] != null ? stats["avgStress"].format("%.0f") : "--"
    51‚Üí                ]
    52‚Üí            );
    53‚Üí            
    54‚Üí            WatchUi.showToast(summary, null);
    55‚Üí        }
    56‚Üí    }function updateTimer() as Void {
    57‚Üí        if (startTime != null && isActive) {
    58‚Üí            var currentTime = Time.now();
    59‚Üí            elapsedTime = currentTime.compare(startTime);
    60‚Üí            biometricsTracker.recordBiometrics();
    61‚Üí            notifications.checkMilestones(elapsedTime);
    62‚Üí            onTimerUpdate.invoke(elapsedTime);
    63‚Üí        }
    64‚Üí    }
    65‚Üí
    66‚Üí    function getElapsedTime() as Number {
    67‚Üí        return elapsedTime;
    68‚Üí    }
    69‚Üí
    70‚Üí    function isActiveFast() as Boolean {
    71‚Üí        return isActive;
    72‚Üí    }
    73‚Üí
    74‚Üí    function restoreState() as Void {
    75‚Üí        var storedIsActive = Storage.getValue("isActiveFast") as Boolean?;
    76‚Üí        var storedStartTime = Storage.getValue("fastStartTime") as Number?;
    77‚Üí
    78‚Üí        if (storedIsActive == true && storedStartTime != null) {
    79‚Üí            startTime = new Time.Moment(storedStartTime);
    80‚Üí            isActive = true;
    81‚Üí            timer.start(method(:updateTimer), 1000, true);
    82‚Üí        }
    83‚Üí    }
    84‚Üí}
