     7‚Üí    private var heartRateIterator as SensorHistory.SensorHistoryIterator?;
     8‚Üí    private var stressIterator as SensorHistory.SensorHistoryIterator?;
     9‚Üí
    10‚Üí    function initialize() {
    11‚Üí        if (System.getDeviceSettings().heartRateEnabled) {
    12‚Üí            heartRateIterator = new SensorHistory.HeartRateIterator({:period => 5}); // Sample every 5 minutes
    13‚Üí        }
    14‚Üí        if (Toybox.SensorHistory has :StressHistoryIterator) {
    15‚Üí            stressIterator = new SensorHistory.StressHistoryIterator({:period => 5});
    16‚Üí        }
    17‚Üí    }
    18‚Üí
    19‚Üí    function getCurrentHeartRate() as Number? {
    20‚Üí        if (heartRateIterator != null) {
    21‚Üí            var sample = heartRateIterator.next();
    22‚Üí            if (sample != null) {
    23‚Üí                return sample.data;
    24‚Üí            }
    25‚Üí        }
    26‚Üí        return null;
    27‚Üí    }
    28‚Üí
    29‚Üí    function getCurrentStressLevel() as Number? {
    30‚Üí        if (stressIterator != null) {
    31‚Üí            var sample = stressIterator.next();
    32‚Üí            if (sample != null) {
    33‚Üí                return sample.data;
    34‚Üí            }
    35‚Üí        }
    36‚Üí        return null;
    37‚Üí    }
    38‚Üí
    39‚Üí    function recordBiometrics() as Void {
    40‚Üí        var hr = getCurrentHeartRate();
    41‚Üí        var stress = getCurrentStressLevel();
    42‚Üí        
    43‚Üí        if (hr != null || stress != null) {
    44‚Üí            var timestamp = Time.now().value();
    45‚Üí            var metrics = {
    46‚Üí                "timestamp" => timestamp,
    47‚Üí                "heartRate" => hr,
    48‚Üí                "stress" => stress
    49‚Üí            };
    50‚Üí            
    51‚Üí            var history = Storage.getValue("biometricHistory") as Array?;
    52‚Üí            if (history == null) {
    53‚Üí                history = [];
    54‚Üí            }
    55‚Üí            history.add(metrics);
    56‚Üí            
    57‚Üí            // Keep only last 24 hours of data
    58‚Üí            if (history.size() > 288) { // 288 = 24 hours * 12 samples per hour
    59‚Üí                history = history.slice(history.size() - 288, null);
    60‚Üí            }
    61‚Üí            
    62‚Üí            Storage.setValue("biometricHistory", history);
    63‚Üí        }
    64‚Üí    }
    65‚Üí
    66‚Üí    function getSessionStats(startTime as Time.Moment, endTime as Time.Moment) as Dictionary {
    67‚Üí        var history = Storage.getValue("biometricHistory") as Array?;
    68‚Üí        if (history == null) {
    69‚Üí            return {};
    70‚Üí        }
    71‚Üí
    72‚Üí        var hrSum = 0;
    73‚Üí        var hrCount = 0;
    74‚Üí        var stressSum = 0;
    75‚Üí        var stressCount = 0;
    76‚Üí        var startValue = startTime.value();
    77‚Üí        var endValue = endTime.value();
    78‚Üí
    79‚Üí        for (var i = 0; i < history.size(); i++) {
    80‚Üí            var entry = history[i];
    81‚Üí            if (entry["timestamp"] >= startValue && entry["timestamp"] <= endValue) {
    82‚Üí                if (entry["heartRate"] != null) {
    83‚Üí                    hrSum += entry["heartRate"];
    84‚Üí                    hrCount++;
    85‚Üí                }
    86‚Üí                if (entry["stress"] != null) {
    87‚Üí                    stressSum += entry["stress"];
    88‚Üí                    stressCount++;
    89‚Üí                }
    90‚Üí            }
    91‚Üí        }
    92‚Üí
    93‚Üí        return {
    94‚Üí            "avgHeartRate" => hrCount > 0 ? hrSum / hrCount : null,
    95‚Üí            "avgStress" => stressCount > 0 ? stressSum / stressCount : null
    96‚Üí        };
    97‚Üí    }
    98‚Üí}
